import React from 'react';

const TelegramAuth = () => {
  const sessionToken =
    '929b877f8fc97fcba20946fb89bd69a10eb4ba1649ae48620d141e6c';
  const botLink = `https://t.me/bazadromru_true_bot?start=${sessionToken}`;
  const qrSrc = `https://api.qrserver.com/v1/create-qr-code/?size=200x200&data=${encodeURIComponent(botLink)}`;

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-50">
      <div className="bg-white p-6 rounded-xl shadow-md w-[350px] text-center">
        <h1 className="text-lg font-semibold mb-4">Подтверждение входа</h1>

        {/* QR Code */}
        <a href={botLink}>
          <img
            src={qrSrc}
            alt="QR Code"
            className="border-2 border-blue-300 rounded-md p-2 hover:scale-105 transition-transform duration-200"
          />
        </a>
        <p className="text-xs text-gray-500 mt-2">
          Нажмите или отсканируйте QR-код
        </p>

        {/* Steps */}
        <ol className="text-sm text-left mt-4 space-y-1">
          <li>1. Перейдите по QR-коду в Telegram</li>
          <li>2. Запустите бота Drom</li>
          <li>3. В чате нажмите "Подтвердить номер"</li>
        </ol>

        {/* Back link */}
        <button
          onClick={() => window.history.back()}
          className="mt-6 text-blue-600 text-sm hover:underline"
        >
          ← вернуться назад
        </button>
      </div>
    </div>
  );
};

export default TelegramAuth;
